mapper.insert:

	SELECT SEQ_BOARD.NEXTVAL FROM DUAL

INSERT INTO TBL_BOARD(
		BNO
		, TITLE
		, CONTENT
		, WRITER
)
VALUES(
		#{bno}
		, #{title}
		, #{content}
		, #{writer}
)

mapper.update:
UPDATE TBL_BOARD
SET TITLE = #{title}
	, CONTENT = #{content}
	, WRITER = #{writer}
	, UPDATEDATE = SYSDATE
WHERE BNO = #{bno}
